import React from "react";
import './App.css';
import Axios from "axios";
import { useState } from 'react';

const App = () => {
  const [name, setName] = useState("");
  const [age, setAge] = useState(0);
  const [gender, setGender] = useState("");
  const [id, setId] = useState("");
  const [fileName, setFileName] = useState("Select Files");
  const [message, setMessage] = useState("");
  const [buttonColor, setButtonColor] = useState("");

  const handleFileChange = (event) => {
    if (event.target.files && event.target.files.length > 0) {
      const file = event.target.files[0];
      setFileName(file.name);
      // Optionally set ID or perform other operations with the file
      setId("driveurl1");
    } else {
      setFileName('Select Files');
    }
  };


  const handleSubmit = (event) => {
    event.preventDefault();
    setButtonColor('red');

    Axios.post("http://localhost:3001/createUsers", {
        name,
        age,
        gender,
        id,
      })
      .then(() => {
        setMessage('Successfully uploaded to drive');
        setButtonColor('green');
        setName("");
        setAge("");
        setGender("");
        setId("");
        setTimeout(()=>{
          setMessage('');
          setButtonColor('');
        },3000);
      })
      .catch(err => {
        setMessage('Not uploaded to drive');
        console.log("Couldn't upload to drive", err);
      });
  };

  return (
    <div className="App">
      <div>
        <h2>Enter Student details:</h2><br />
        <p>{message}</p>

          <input type="text" placeholder="Name.." value={name} onChange={(event) => setName(event.target.value)} /><br/>
          <input type="number" placeholder="Age.." value={age} onChange={(event) => setAge(event.target.value)} /><br/>
          <input type="text" placeholder="Gender.." value={gender} onChange={(event) => setGender(event.target.value)} /><br/>
          <input type="file" id="fileInput" onChange={handleFileChange} /><br/>
          <label htmlFor="fileInput">{fileName}</label>

          <button type="submit" id="myButton" onClick={handleSubmit} style={{ backgroundColor: buttonColor }}>Submit</button>

      </div>
    </div>
  );
}

export default App;
