import React, { useState, useRef } from "react";
import './App.css';
import Axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';

const App = () => {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [gender, setGender] = useState("");
  const [fileName, setFileName] = useState("Select Files");
  const [message, setMessage] = useState("");
  const [buttonColor, setButtonColor] = useState("");
  const [file, setFile] = useState(null);
  const [previewLink, setPreviewLink] = useState("");
  const fileInputRef = useRef(null);

  const handleFileChange = (event) => {
    if (event.target.files && event.target.files.length > 0) {
      const file = event.target.files[0];
      setFile(file);
      setFileName(file.name);
    } else {
      setFileName('Select Files');
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setButtonColor('red');
    const formData = new FormData();
    formData.append('name', name);
    formData.append('age', age);
    formData.append('gender', gender);
    formData.append('file', file);

    try {
      const response = await Axios.post('http://localhost:3001/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      const url = response.data.fileUrl;
      setPreviewLink(url);

      setMessage('Successfully uploaded to drive and saved in backend');
      setButtonColor('green');
      setName("");
      setAge("");
      setGender("");
      setFileName("Select Files");
      setFile(null);
      if (fileInputRef.current) {
        fileInputRef.current.value = null;
      }

      setTimeout(() => {
        setMessage('');
        setButtonColor('');
        //setPreviewLink('');
      }, 5000);
    } catch (err) {
      setMessage('Not uploaded to drive or backend');
      setTimeout(() => {
        setMessage('');
        setButtonColor('');
        //setPreviewLink('');
      }, 5000);
      console.log("Couldn't upload to drive or backend", err);
    }
  };

  return (
    <div className="App">
      <div className="container p-5 my-5 border text-center">
        <h2>Enter Student details:</h2><br />
        {message && <p className={`alert ${buttonColor === 'green' ? 'alert-success' : 'alert-danger'} text-center`}>{message}</p>}

        <form className="" onSubmit={handleSubmit}>
          <input className="col form-control" type="text" placeholder="Name.." value={name} onChange={(event) => setName(event.target.value)} /><br/><br/>
          <input  className="col form-control" type="number" placeholder="Age.." value={age} onChange={(event) => setAge(event.target.value)} /><br/><br/>
          <select className="col form-select" value={gender} onChange={(event) => setGender(event.target.value)}>
            <option value="">Select Gender..</option>
            <option value="m">Male</option>
            <option value="f">Female</option>
            <option value="o">Other</option>
          </select><br/><br/>
          <input className="col text-center form-control" type="file" id="fileInput" onChange={handleFileChange} ref={fileInputRef} /><br/>
          <label className="col" hidden htmlFor="fileInput">{fileName}</label><br/>

          <button className="btn btn-outline-primary " type="submit" id="myButton" style={{ backgroundColor: buttonColor }}>Submit</button>
        </form>

      </div>
      <div className="text-center">{previewLink && <p><a href={previewLink} className="btn btn-info" target="_blank" rel="noopener noreferrer">Preview of the recently uploaded file</a></p>}</div>
    </div>
  );
}

export default App;
