import './App.css';
import Axios from "axios";
import { useState,useEffect } from 'react';

const App =()=> {
  const[listOfUsers,setListOfUsers]=useState([]);
  const[name,setName]=useState("");
  const[age,setAge]=useState(0);
  const[gender,setGender]=useState("");
  const[fileName,setFileName]=useState('Select Files');
  const[message,setMessage]=useState("");
  const[buttonColor,setButtonColor]=useState('#4caf50');

  //we need to update list of users
  useEffect(() => {
    Axios.get("http://localhost:3001/getUsers")
      .then((response) => {
        setListOfUsers(response.data);
      });
  }, []);

  const handleFileChange = (event) => {
    if (event.target.files && event.target.files.length > 0) {
      const name = event.target.files.length === 1 
        ? event.target.files[0].name 
        : `${event.target.files.length} files selected`;
      setFileName(name);
    } else {
      setFileName('Select Files');
    }
  };

  const handleSubmit=async(event)=>{
    event.preventDefault();
    setButtonColor('red');
    const formData=new formData(event.target);
    try{
      Axios.post("http://localhost:3002/createUsers",{
        name,
        age,
        gender,
        id,
      }).then((response)=>{
        setListOfUsers([
           ...listOfUsers,
           {
            name,
            age,
            gender,
            id,
           }, 
        ]);
      });
      setMessage('Successfully uploaded to drive');
      setButtonColor('green');
      setFileName('Select Files');
    } catch (error) {
      setMessage(`Was not uploaded: ${error.message}`);
    }
    };



  return (
    <div className="App">
     <div>
      <h2>Enter Student details:</h2><br/>
      <p>{message}</p>
      <form onSubmit={handleSubmit}>
      <input type="text" placeholder="Name.." onChange={(event)=>{
        setName(event.target.value);
      }}/><br/>
      <input type="number" placeholder="Age.." onChange={(event)=>{
        setAge(event.target.value);
      }}/> 
      <input type="text" placeholder="Gender.." onChange={(event)=>{
        setGender(event.target.value);
      }}/> 
      <input type="file" id="fileInput" placeholder="Upload File" onChange={(event)=>{
        setFile(event.target.value);
      }}/>
      <button type="submit" id="myButton" style={{backgroundColor:buttonColor}}>Submit</button>
      </form>

     </div>
    </div>
  );
}

export default App;
